import React, { useState } from 'react';import logo from '../assets/logo.png.png';const Header = () => {  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);  const toggleMobileMenu = () => {    setMobileMenuOpen(!mobileMenuOpen);  };  return (    <header>      <div className="container">        <div className="header-inner">          <div className="logo-section">            <img src={logo} alt="Railroad Cotton Exchange" />            <span>Railroad Cotton Exchange</span>          </div>                    <nav className="desktop-nav">            <span className="active">Home</span>            <span>Shop</span>            <span>Contact us</span>            <span>About us</span>          </nav>                    <div className="account-desktop">            <button>              <i className="fas fa-shopping-cart"></i>              Cart            </button>            <button>              <i className="fas fa-user-circle"></i>              My Account            </button>          </div>          <button className="mobile-menu-button" onClick={toggleMobileMenu}>            <i className="fas fa-bars"></i>          </button>        </div>      </div>      {/* Mobile Menu */}      <div className={`mobile-menu ${mobileMenuOpen ? 'show' : ''}`}>        <div className="mobile-menu-header">          <div className="logo-section">            <img src={logo} alt="Railroad Cotton Exchange" />            <span>Railroad Cotton Exchange</span>          </div>          <button className="mobile-menu-close" onClick={toggleMobileMenu}>            <i className="fas fa-times"></i>          </button>        </div>                <nav className="mobile-menu-nav">          <span className="active">Home</span>          <span>Shop</span>          <span>Contact us</span>          <span>About us</span>        </nav>                <div className="mobile-menu-account">          <button>            <i className="fas fa-shopping-cart"></i>            Cart          </button>          <button>            <i className="fas fa-user-circle"></i>            My Account          </button>        </div>      </div>    </header>  );
};

export default Header;
