import React, { useState, useEffect } from 'react';
import { useParams, useLocation } from 'react-router-dom';
import fabricImg from '../assets/products.png';
import '../styles/ProductDetail.css';

const ProductDetail = () => {
  const { id } = useParams();
  const location = useLocation();
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [quantity, setQuantity] = useState(1);
  const [activeTab, setActiveTab] = useState('description');
  const [relatedProducts, setRelatedProducts] = useState([]);

  useEffect(() => {
    // If we have product data from navigation state, use it
    if (location.state?.product) {
      setProduct(location.state.product);
      setLoading(false);
    } else {
      // Otherwise fetch the product data
      fetchProductDetails();
    }
    // Fetch related products
    fetchRelatedProducts();
  }, [id, location.state]);

  const fetchProductDetails = async () => {
    try {
      const response = await fetch(`http://localhost:3000/api/products/${id}`);
      if (!response.ok) {
        throw new Error('Failed to fetch product details');
      }
      const data = await response.json();
      setProduct(data);
      setLoading(false);
    } catch (err) {
      setError(err.message);
      setLoading(false);
    }
  };

  const fetchRelatedProducts = async () => {
    try {
      const response = await fetch('http://localhost:3000/api/products');
      if (!response.ok) {
        throw new Error('Failed to fetch related products');
      }
      const data = await response.json();
      // Filter out the current product and get up to 3 related products
      const filteredProducts = data
        .filter(p => p.id !== parseInt(id))
        .slice(0, 3);
      setRelatedProducts(filteredProducts);
    } catch (err) {
      console.error('Error fetching related products:', err);
    }
  };

  const incrementQuantity = () => {
    setQuantity(quantity + 1);
  };
  
  const decrementQuantity = () => {
    if (quantity > 1) {
      setQuantity(quantity - 1);
    }
  };

  if (loading) return <div>Loading product details...</div>;
  if (error) return <div>Error: {error}</div>;
  if (!product) return <div>Product not found</div>;

  return (
    <div className="product-detail-container">
      <div className="product-detail-content">
        <div className="product-image-section">
          <img src={fabricImg} alt={product.name} className="product-detail-image" />
        </div>
        <div className="product-info-section">
          <h1 className="product-name">{product.name}</h1>
          <p className="product-price">${product.price}</p>
          <div className="product-stock">
            <span>In Stock: {product.stock}</span>
          </div>
          <p className="product-description">{product.description}</p>
          <div className="product-actions">
            <button className="add-to-cart-button">
              Add to Cart
            </button>
            <button className="buy-now-button">
              Buy Now
            </button>
          </div>
        </div>
      </div>
      
      {/* Breadcrumbs */}
      <div className="breadcrumb-container">
        <div className="breadcrumbs">
          <span>Products</span>
          <span>/</span>
          <span>{product.name}</span>
          <span>/</span>
          <span className="current">Product Detail</span>
        </div>
      </div>
      
      {/* Product Detail Section */}
      <div className="product-main-container">
        <div className="product-image-container">
          <img src={fabricImg} alt={product.name} className="product-main-image" />
        </div>
        
        <div className="product-info-container">
          {/* Product Specifications */}
          <div className="product-specs">
            <div className="spec-item">
              <span className="spec-label">Name:</span>
              <span className="spec-value">{product.name}</span>
            </div>
            <div className="spec-item">
              <span className="spec-label">Price:</span>
              <span className="spec-value">${product.price}</span>
            </div>
            <div className="spec-item">
              <span className="spec-label">Stock:</span>
              <span className="spec-value">{product.stock} units available</span>
            </div>
            <div className="spec-item">
              <span className="spec-label">Description:</span>
              <span className="spec-value">{product.description}</span>
            </div>
          </div>
          
          {/* Quantity Selector */}
          <div className="quantity-selector">
            <button className="quantity-btn" onClick={decrementQuantity}>âˆ’</button>
            <span className="quantity">{quantity}</span>
            <button className="quantity-btn" onClick={incrementQuantity}>+</button>
          </div>
        </div>
      </div>
      
      {/* Description Tab */}
      <div className="product-tabs">
        <div className="tab-buttons">
          <button 
            className={activeTab === 'description' ? 'tab-btn active' : 'tab-btn'} 
            onClick={() => setActiveTab('description')}
          >
            Description
          </button>
        </div>
        
        <div className="tab-content">
          {activeTab === 'description' && (
            <div className="description-tab">
              <p>{product.description}</p>
            </div>
          )}
        </div>
      </div>
      
      {/* Related Products */}
      <div className="related-products">
        <h2>Related Products</h2>
        
        <div className="products-grid">
          {relatedProducts.map((relatedProduct) => (
            <div key={relatedProduct.id} className="related-product">
              <div className="product-img-container">
                <img src={fabricImg} alt={relatedProduct.name} />
                <button className="wishlist-btn">
                  <i className="far fa-heart"></i>
                </button>
              </div>
              <h3>{relatedProduct.name}</h3>
              <p className="product-price">${relatedProduct.price}</p>
              <p className="product-desc">{relatedProduct.description}</p>
              <button 
                className="view-product-btn"
                onClick={() => window.location.href = `/product/${relatedProduct.id}`}
              >
                View Product
              </button>
            </div>
          ))}
        </div>
        
        <div className="explore-more-container">
          <button className="explore-more-btn">
            Explore More
            <i className="fas fa-arrow-right"></i>
          </button>
        </div>
      </div>
    </div>
  );
};

export default ProductDetail;
